dev = serial.Serial('/dev/ttyAMA0',baudrate=115200,timeout=0)

CMD_FW    = 0x00
CMD_DUTY  = 0x01
CMD_SERVO = 0x02
CMD_PULSE = 0x03
CMD_RESET = 0xff

def _write(arr):
  dev.write(''.join(chr(x) for x in arr))

def _to_bytes(num):
  return list(ord(x) for x in list(struct.pack('>H',num)))

def duty(pin, duty, freq):
  _write([0xff,0xff,CMD_DUTY,pin,duty] + list(ord(x) for x in list(struct.pack('>H',freq))))

def pulse(pin, on_time, off_time):
  _write([0xff,0xff,CMD_PULSE,pin] + _to_bytes(on_time) + _to_bytes(off_time))   

def servo(pin, pulse):
  _write([0xff,0xff,CMD_SERVO,pin] + _to_bytes(pulse))
